#include <stdio.h>

void main()
{

    int num1, num2;
    printf("enter first number:");
    scanf("%d", &num1);
    printf("enter second number:");
    scanf("%d", &num2);
    printf("Sum = %d\n", num1 + num2);
    printf("their multiplication is %d\n", num1*num2);
    printf("their subtraction is %d\n", num1-num2);
    printf("their Quotient is %d\n", num1/num2);
}